mapper.select:
SELECT 		U_ID		,USER_NAME		,EMAIL		,PHONE  
FROM 		USER_INFO
WHERE 		U_ID=#{uId}

SELECT		COUNT(1)
FROM		USER_INFO
WHERE		USER_PW=#{checkPw}

mapper.update:
		UPDATE 		USER_INFO
		SET			USER_NAME=#{userName}		,EMAIL=#{email}		,PHONE=#{phone}

    				,USER_PW=#{userPw}

		WHERE		U_ID=#{uId}
